mestamp\";i:1716397200;s:18:\"end_unix_timestamp\";i:1716404400;s:8:\"location\";a:4:{s:8:\"location\";s:6:\"Online\";s:7:\"country\";s:2:\"DE\";s:8:\"latitude\";d:50.22;s:9:\"longitude\";d:6.66;}}i:4;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:19:\"WP Meetup Eindhoven\";s:3:\"url\";s:66:\"https://www.meetup.com/wordpress-meetup-eindhoven/events/299874130\";s:6:\"meetup\";s:26:\"WordPress Meetup Eindhoven\";s:10:\"meetup_url\";s:50:\"https://www.meetup.com/wordpress-meetup-eindhoven/\";s:4:\"date\";s:19:\"2024-05-27 19:30:00\";s:8:\"end_date\";s:19:\"2024-05-27 21:00:00\";s:20:\"start_unix_timestamp\";i:1716831000;s:18:\"end_unix_timestamp\";i:1716836400;s:8:\"location\";a:4:{s:8:\"location\";s:22:\"Eindhoven, Netherlands\";s:7:\"country\";s:2:\"nl\";s:8:\"latitude\";d:51.445515;s:9:\"longitude\";d:5.460191;}}}}','no'),(324,'_transient_timeout_feed_0d2db5fea5a8633b56992c916819b198','1715201317','no'),(325,'_transient_feed_0d2db5fea5a8633b56992c916819b198','a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:52:\"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n\n \n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:8:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:32:\"Blog – WordPress.org Français\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:24:\"https://fr.wordpress.org\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_b